_injectionAdaptersLoaded":false},"children":[],"_data":{"id":98,"menuId":3,"menuItemId":16,"parentId":0,"seq":2},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":20,"contextId":1,"type":"NMI_TYPE_SUBMISSIONS","titleLocaleKey":"about.submissions"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":99,"menuId":3,"menuItemId":20,"parentId":0,"seq":3},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":29,"contextId":1,"type":"NMI_TYPE_CUSTOM","path":"huongdan","content":{"en_US":"","vi_VN":"<p><strong>Quy tr\\u00ecnh g\\u1eedi b\\u00e0i<\\/strong><\\/p>\\r\\n<ol>\\r\\n<li class=\\"show\\"><span style=\\"font-weight: 400;\\"> T\\u00e1c gi\\u1ea3 g\\u1eedi b\\u00e0i b\\u00e1o, c\\u00f4ng tr\\u00ecnh nghi\\u00ean c\\u1ee9u cho cho t\\u1ea1p ch\\u00ed th\\u00f4ng qua website<\\/span><\\/li>\\r\\n<\\/ol>\\r\\n<ol start=\\"2\\">\\r\\n<li class=\\"show\\"><span style=\\"font-weight: 400;\\"> Th\\u01b0 k\\u00fd c\\u00f3 tr\\u00e1ch nhi\\u1ec7m xem x\\u00e9t c\\u00e1c n\\u1ed9i dung: c\\u00e1c b\\u00e0i kh\\u00f4ng \\u0111\\u00fang quy c\\u00e1ch <\\/span>(theo th\\u1ec3 l\\u1ec7 g\\u1eedi b\\u00e0i v\\u00e0 \\u0111\\u1ecbnh d\\u1ea1ng m\\u1eabu), ki\\u1ec3m tra \\u0111\\u1ea1o v\\u0103n (kh\\u00f4ng qu\\u00e1 20%) ho\\u1eb7c n\\u1ed9i dung kh\\u00f4ng \\u0111\\u00e1p \\u1ee9ng y\\u00eau c\\u1ea7u c\\u1ee7a m\\u1ed9t b\\u00e0i b\\u00e1o khoa h\\u1ecdc. Th\\u01b0 k\\u00fd c\\u00f3 tr\\u00e1ch nhi\\u1ec7m x\\u00e1c nh\\u1eadn \\u0111\\u1ea1t y\\u00eau c\\u1ea7u qua email (t\\u1ef1 \\u0111\\u1ed9ng) cho t\\u00e1c gi\\u1ea3 ho\\u1eb7c y\\u00eau c\\u1ea7u c\\u1ea7n ch\\u1ec9nh s\\u1eeda cho ph\\u00f9 h\\u1ee3p;<\\/li>\\r\\n<\\/ol>\\r\\n<ol start=\\"3\\">\\r\\n<li class=\\"show\\"><span style=\\"font-weight: 400;\\">C\\u0103n c\\u1ee9 v\\u00e0o n\\u1ed9i dung chuy\\u00ean m\\u00f4n c\\u1ee7a b\\u00e0i b\\u00e1o, <\\/span>th\\u01b0 k\\u00fd g\\u1eedi b\\u00e0i b\\u00e1o cho c\\u00e1c chuy\\u00ean gia \\u0111\\u1ec3 ph\\u1ea3n bi\\u1ec7n. Th\\u1eddi gian ph\\u1ea3n bi\\u1ec7n kh\\u00f4ng qu\\u00e1 45 ng\\u00e0y k\\u1ec3 t\\u1eeb ng\\u00e0y nh\\u1eadn \\u0111\\u01b0\\u1ee3c b\\u00e0i b\\u00e1o<\\/li>\\r\\n<\\/ol>\\r\\n<ol start=\\"4\\">\\r\\n<li class=\\"show\\"><span style=\\"font-weight: 400;\\"> M\\u1ed7i b\\u00e0i b\\u00e1o \\u0111\\u01b0\\u1ee3c g\\u1eedi cho 2 ph\\u1ea3n bi\\u1ec7n \\u0111\\u1ed9c l\\u1eadp<\\/span><\\/li>\\r\\n<\\/ol>\\r\\n<p><span style=\\"font-weight: 400;\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; a) Ti\\u00eau chu\\u1ea9n ph\\u1ea3n bi\\u1ec7n b\\u00e0i b\\u00e1o khoa h\\u1ecdc l\\u00e0 ng\\u01b0\\u1eddi c\\u00f3 h\\u1ecdc h\\u00e0m GS, PGS ho\\u1eb7c h\\u1ecdc v\\u1ecb Ti\\u1ebfn S\\u0129 chuy\\u00ean ng\\u00e0nh ho\\u1eb7c GVC-ThS c\\u00f3 chuy\\u00ean ng\\u00e0nh ph\\u00f9 h\\u1ee3p;<\\/span><\\/p>\\r\\n<p><span style=\\"font-weight: 400;\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; b) N\\u1ed9i dung nh\\u1eadn x\\u00e9t, ph\\u1ea3n bi\\u1ec7n b\\u00e0i b\\u00e1o khoa h\\u1ecdc bao g\\u1ed3m:<\\/span><\\/p>\\r\\n<p><span style=\\"font-weight: 400;\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; - \\u00dd ngh\\u0129a, gi\\u00e1 tr\\u1ecb khoa h\\u1ecdc c\\u1ee7a v\\u1ea5n \\u0111\\u1ec1, n\\u1ed9i dung, k\\u1ebft qu\\u1ea3 nghi\\u00ean c\\u1ee9u;<\\/span><\\/p>\\r\\n<p><span style=\\"font-weight: 400;\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- M\\u1ee9c \\u0111\\u1ed9 \\u0111\\u00e1p \\u1ee9ng c\\u00e1c quy \\u0111\\u1ecbnh v\\u1ec1 b\\u1ed1 c\\u1ee5c v\\u00e0 h\\u00ecnh th\\u1ee9c b\\u00e0i b\\u00e1o;<\\/span><\\/p>\\r\\n<p><span style=\\"font-weight: 400;\\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;- Ki\\u1ebfn ngh\\u1ecb s\\u1eeda ch\\u1eefa, b\\u1ed5 sung.<\\/span><\\/p>\\r\\n<ol start=\\"5\\">\\r\\n<li class=\\"show\\"><span style=\\"font-weight: 400;\\"> Th\\u01b0 k\\u00fd c\\u00f3 tr\\u00e1ch nhi\\u1ec7m chuy\\u1ec3n c\\u00e1c y\\u00eau c\\u1ea7u, \\u0111\\u1ec1 ngh\\u1ecb s\\u1eeda ch\\u1eefa, b\\u1ed5 sung <\\/span>cho t\\u00e1c gi\\u1ea3 b\\u00e0i b\\u00e1o l\\u00ean website v\\u00e0 email t\\u1ef1 \\u0111\\u1ed9ng cho t\\u00e1c gi\\u1ea3. Sau 30 ng\\u00e0y k\\u1ec3 t\\u1eeb khi nh\\u1eadn \\u0111\\u01b0\\u1ee3c y\\u00eau c\\u1ea7u s\\u1eeda ch\\u1eefa, b\\u1ed5 sung, t\\u00e1c gi\\u1ea3 b\\u00e0i b\\u00e1o ph\\u1ea3i ch\\u1ec9nh s\\u1eeda v\\u00e0 gi\\u1eed l\\u1ea1i b\\u00e0i qua h\\u1ec7 th\\u1ed1ng tr\\u00ean website.<\\/li>\\r\\n<li class=\\"show\\"><span style=\\"font-weight: 400;\\">Ban Bi\\u00ean t\\u1eadp kh\\u00f4ng tr\\u1ea3 l\\u1ea1i b\\u1ea3n th\\u1ea3o n\\u1ebfu b\\u00e0i kh\\u00f4ng \\u0111\\u01b0\\u1ee3c \\u0111\\u0103ng. B\\u00e0i \\u0111\\u1ec1 ngh\\u1ecb \\u0111\\u0103ng <\\/span>s\\u1ed1 n\\u00e0y n\\u1ebfu kh\\u00f4ng s\\u1eafp x\\u1ebfp \\u0111\\u01b0\\u1ee3c s\\u1ebd \\u0111\\u0103ng \\u1edf s\\u1ed1 k\\u1ebf ti\\u1ebfp.<\\/li>\\r\\n<\\/ol>\\r\\n<p><strong>Quy tr\\u00ecnh ph\\u1ea3n bi\\u1ec7n<\\/strong><\\/p>\\r\\n<p align=\\"justify\\">Quy tri\\u0300nh pha\\u0309n bi\\u00ea\\u0323n ki\\u0301nh 2 chi\\u00ea\\u0300u. Danh t\\u00ednh c\\u1ee7a nh\\u1eefng ng\\u01b0\\u1eddi pha\\u0309n bi\\u00ea\\u0323n kh\\u00f4ng \\u0111\\u01b0\\u1ee3c ti\\u1ebft l\\u1ed9 cho c\\u00e1c t\\u00e1c gi\\u1ea3 v\\u00e0 ng\\u01b0\\u1ee3c l\\u1ea1i. \\u0110\\u1ec3 \\u0111\\u01b0\\u1ee3c xu\\u1ea5t b\\u1ea3n, c\\u00e1c b\\u1ea3n th\\u1ea3o \\u0111\\u01b0\\u1ee3c g\\u1eedi \\u0111\\u1ebfn T\\u1ea1p ch\\u00ed ph\\u1ea3i tr\\u1ea3i qua qu\\u00e1 tr\\u00ecnh ki\\u1ec3m tra s\\u01a1 b\\u1ed9 c\\u1ee7a Ban bi\\u00ean t\\u1eadp, th\\u1ea9m \\u0111\\u1ecbnh \\u1ea9n danh b\\u1edfi c\\u00e1c nh\\u00e0 khoa h\\u1ecdc c\\u00f3 chuy\\u00ean m\\u00f4n ph\\u00f9 h\\u1ee3p, ch\\u1ec9nh s\\u1eeda b\\u1ea3n th\\u1ea3o, sau c\\u00f9ng l\\u00e0 quy\\u1ebft \\u0111\\u1ecbnh c\\u1ee7a T\\u1ed5ng Bi\\u00ean t\\u1eadp.<\\/p>\\r\\n<ul>\\r\\n<li class=\\"show\\">B\\u01b0\\u1edbc 1: Ti\\u1ebfp nh\\u1eadn b\\u00e0i g\\u1eedi: B\\u00e0i g\\u1eedi \\u0111\\u01b0\\u1ee3c g\\u1eedi \\u0111\\u1ebfn T\\u1ea1p ch\\u00ed v\\u00e0 ti\\u1ebfp nh\\u1eadn b\\u1edfi Ban bi\\u00ean t\\u1eadp. Ban bi\\u00ean t\\u1eadp xem x\\u00e9t b\\u00e0i g\\u1eedi ban \\u0111\\u1ea7u \\u0111\\u1ec3 \\u0111\\u1ea3m b\\u1ea3o n\\u00f3 tu\\u00e2n theo c\\u00e1c y\\u00eau c\\u1ea7u c\\u01a1 b\\u1ea3n v\\u1ec1 \\u0111\\u1ecbnh d\\u1ea1ng v\\u00e0 ph\\u1ea1m vi c\\u1ee7a t\\u1ea1p ch\\u00ed.<\\/li>\\r\\n<li class=\\"show\\">B\\u01b0\\u1edbc 2: Ph\\u00e2n lo\\u1ea1i v\\u00e0 g\\u1eedi cho ph\\u1ea3n bi\\u1ec7n: Ban bi\\u00ean t\\u1eadp quy\\u1ebft \\u0111\\u1ecbnh xem li\\u1ec7u b\\u00e0i g\\u1eedi \\u0111\\u00e1ng \\u0111\\u1ec3 ti\\u1ebfp t\\u1ee5c v\\u00e0o qu\\u00e1 tr\\u00ecnh ph\\u1ea3n bi\\u1ec7n hay kh\\u00f4ng. C\\u00e1c b\\u00e0i g\\u1eedi ti\\u1ec1m n\\u0103ng \\u0111\\u01b0\\u1ee3c g\\u1eedi cho c\\u00e1c nh\\u00e0 ph\\u1ea3n bi\\u1ec7n c\\u00f3 chuy\\u00ean m\\u00f4n ph\\u00f9 h\\u1ee3p v\\u1edbi n\\u1ed9i dung c\\u1ee7a b\\u00e0i.<\\/li>\\r\\n<li class=\\"show\\">B\\u01b0\\u1edbc 3: Ph\\u1ea3n bi\\u1ec7n \\u1ea9n danh: C\\u00e1c nh\\u00e0 ph\\u1ea3n bi\\u1ec7n \\u0111\\u00e1nh gi\\u00e1 b\\u00e0i g\\u1eedi m\\u00e0 kh\\u00f4ng ti\\u1ebft l\\u1ed9 danh t\\u00ednh c\\u1ee7a m\\u00ecnh cho t\\u00e1c gi\\u1ea3, v\\u00e0 ng\\u01b0\\u1ee3c l\\u1ea1i. Qu\\u00e1 tr\\u00ecnh ph\\u1ea3n bi\\u1ec7n \\u1ea9n danh \\u0111\\u1ea3m b\\u1ea3o t\\u00ednh c\\u00f4ng b\\u1eb1ng v\\u00e0 kh\\u00e1ch quan trong qu\\u00e1 tr\\u00ecnh \\u0111\\u00e1nh gi\\u00e1 b\\u00e0i g\\u1eedi.<\\/li>\\r\\n<li class=\\"show\\">B\\u01b0\\u1edbc 4: \\u0110\\u00e1nh gi\\u00e1 v\\u00e0 cung c\\u1ea5p ph\\u1ea3n h\\u1ed3i: Nh\\u00e0 ph\\u1ea3n bi\\u1ec7n \\u0111\\u1ecdc v\\u00e0 \\u0111\\u00e1nh gi\\u00e1 b\\u00e0i g\\u1eedi, sau \\u0111\\u00f3 cung c\\u1ea5p ph\\u1ea3n h\\u1ed3i chi ti\\u1ebft v\\u00e0 \\u00fd ki\\u1ebfn v\\u1ec1 ch\\u1ea5t l\\u01b0\\u1ee3ng, ph\\u01b0\\u01a1ng ph\\u00e1p, n\\u1ed9i dung v\\u00e0 b\\u1ea5t k\\u1ef3 c\\u1ea3i thi\\u1ec7n n\\u00e0o. Ph\\u1ea3n h\\u1ed3i n\\u00e0y gi\\u00fap t\\u00e1c gi\\u1ea3 c\\u1ea3i thi\\u1ec7n v\\u00e0 ho\\u00e0n thi\\u1ec7n b\\u00e0i g\\u1eedi c\\u1ee7a m\\u00ecnh.<\\/li>\\r\\n<li class=\\"show\\">B\\u01b0\\u1edbc 5: Xem x\\u00e9t l\\u1ea1i v\\u00e0 ch\\u1ec9nh s\\u1eeda: T\\u00e1c gi\\u1ea3 nh\\u1eadn \\u0111\\u01b0\\u1ee3c ph\\u1ea3n h\\u1ed3i t\\u1eeb nh\\u00e0 ph\\u1ea3n bi\\u1ec7n v\\u00e0 c\\u00f3 th\\u1ec3 s\\u1eeda \\u0111\\u1ed5i b\\u00e0i g\\u1eedi \\u0111\\u1ec3 c\\u1ea3i thi\\u1ec7n ch\\u1ea5t l\\u01b0\\u1ee3ng v\\u00e0 \\u0111\\u00e1p \\u1ee9ng c\\u00e1c y\\u00eau c\\u1ea7u c\\u1ee7a t\\u1ea1p ch\\u00ed. T\\u00e1c gi\\u1ea3 c\\u00f3 th\\u1ec3 g\\u1eedi phi\\u00ean b\\u1ea3n ch\\u1ec9nh s\\u1eeda ho\\u1eb7c gi\\u1ea3i th\\u00edch l\\u00fd do n\\u1ebfu kh\\u00f4ng \\u0111\\u1ed3ng \\u00fd v\\u1edbi c\\u00e1c \\u00fd ki\\u1ebfn c\\u1ee7a nh\\u00e0 ph\\u1ea3n bi\\u1ec7n.<\\/li>\\r\\n<li class=\\"show\\">B\\u01b0\\u1edbc 6: Quy\\u1ebft \\u0111\\u1ecbnh xu\\u1ea5t b\\u1ea3n: Cu\\u1ed1i c\\u00f9ng, quy\\u1ebft \\u0111\\u1ecbnh v\\u1ec1 vi\\u1ec7c xu\\u1ea5t b\\u1ea3n b\\u00e0i g\\u1eedi thu\\u1ed9c v\\u1ec1 T\\u1ed5ng bi\\u00ean t\\u1eadp. D\\u1ef1a tr\\u00ean nh\\u1eadn x\\u00e9t c\\u1ee7a nh\\u00e0 ph\\u1ea3n bi\\u1ec7n v\\u00e0 phi\\u00ean b\\u1ea3n ch\\u1ec9nh s\\u1eeda cu\\u1ed1i c\\u00f9ng c\\u1ee7a t\\u00e1c gi\\u1ea3, T\\u1ed5ng bi\\u00ean t\\u1eadp quy\\u1ebft \\u0111\\u1ecbnh xem li\\u1ec7u b\\u00e0i g\\u1eedi c\\u00f3 \\u0111\\u00e1ng xu\\u1ea5t b\\u1ea3n trong t\\u1ea1p ch\\u00ed hay kh\\u00f4ng.<\\/li>\\r\\n<\\/ul>\\r\\n<p>Quy tr\\u00ecnh ph\\u1ea3n bi\\u1ec7n tu\\u00e2n theo nguy\\u00ean t\\u1eafc c\\u00f4ng b\\u1eb1ng, \\u0111\\u00e1nh gi\\u00e1 ch\\u1ea5t l\\u01b0\\u1ee3ng v\\u00e0 \\u0111\\u1ea3m b\\u1ea3o t\\u00ednh k\\u1ef9 thu\\u1eadt c\\u1ee7a c\\u00e1c b\\u00e0i g\\u1eedi tr\\u01b0\\u1edbc khi xu\\u1ea5t b\\u1ea3n.<\\/p>\\r\\n<p><strong>L\\u01b0u \\u0111\\u00f4\\u0300 quy tri\\u0300nh pha\\u0309n bi\\u00ea\\u0323n<\\/strong><\\/p>\\r\\n<p><img src=\\"http:\\/\\/tapchikhoahoc.vlute.edu.vn\\/public\\/site\\/images\\/admin\\/artboard-1-2dbbba3843b35c64e176d946fb9c87d2.png\\" alt=\\"\\" width=\\"612\\" height=\\"792\\"><\\/p>"},"remoteUrl":{"en_US":"","vi_VN":""},"title":{"en_US":"Tutorials","vi_VN":"H\\u01b0\\u1edbng d\\u1eabn"}},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":100,"menuId":3,"menuItemId":29,"parentId":0,"seq":4,"title":{"en_US":"Tutorials","vi_VN":"H\\u01b0\\u1edbng d\\u1eabn"}},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":17,"contextId":1,"type":"NMI_TYPE_ANNOUNCEMENTS","titleLocaleKey":"manager.announcements"},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},"children":[],"_data":{"id":101,"menuId":3,"menuItemId":17,"parentId":0,"seq":5},"_hasLoadableAdapters":false,"_metadataExtractionAdapters":[],"_extractionAdaptersLoaded":false,"_metadataInjectionAdapters":[],"_injectionAdaptersLoaded":false},{"navigationMenuItem":{"navigationMenuItems":[],"_isDisplayed":true,"_isChildVisible":false,"_data":{"id":23,"contextId":1,"type":"NMI_TYPE_CONTACT","titleLocaleKey":"about.contact"},"_hasLoadableAdapters":false,"_metadataExtracti